下载simplean.js文件到项目中    页面中引入simplean.js脚本  script type="text/javascript" src="***/simplean.js" /script    初始化Simplean实例  var dom = document.querySelector('div.animation');
var sp = Simplean(dom);    配置你想要的动画效果  sp.to({
    width: '200px',
    height: '200px',
    border-radius: '50px'
  });

to(styles[, options])


Simplean(dom).to({
    width: 200px;
    background-color: red;
});
Simplean(dom).to({
    height: 200px;
    border-radius: 50px;
}, {
    ease: 'ease-out'
});



addClass(classNames[, options])


.highlight {
    color: red;
    background: blue;
}

Simplean(dom).addClass('highlight', {
    ease: 'ease-in-out',
    onStart: function () {
        console.log('I will be highlight');
    },
    onStop: function () {
        console.log('I\'m be highlight');
    }
});



removeClass(classNames[, options])


.rotate-30 {
    transform: rotate(30deg)
}

Simplean(dom).removeClass('rotate-30', {
    ease: 'ease-in',
});

